import React, { useState } from "react";

const FileUpload = () => {
    const [fileName, setFileName] = useState("Файл не вибрано");

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        setFileName(file ? file.name : "Файл не вибрано");
    };

    return (
        <div className="flex flex-col items-center space-y-4">
            <label className="text-lg font-semibold">Завантаження файлу</label>
            <div className="flex items-center space-x-2">
                <input
                    type="file"
                    id="fileInput"
                    className="hidden"
                    onChange={handleFileChange}
                />
                <label
                    htmlFor="fileInput"
                    className="px-4 py-2 bg-gray-200 text-gray-700 rounded-lg cursor-pointer"
                >
                    Завантажити файл
                </label>
                <span>{fileName}</span>
            </div>
            <button className="px-6 py-2 bg-blue-600 text-white rounded-lg">
                ОБРОБИТИ
            </button>
        </div>
    );
};

export default FileUpload;
